from scipy.constants import  Avogadro

def dist(r1, r2, L):
    '''Calculate the minimum image distance between two atoms in a periodic cubic system.
    Parameters:
    r1 : The (x, y, z) coordinates of the first atom.
    r2 : The (x, y, z) coordinates of the second atom.
    L (float): The length of the side of the cubic box.
    Returns:
    float: The minimum image distance between the two atoms.
    '''
    # Calculate the coordinate differences
    dr = np.array(r1) - np.array(r2)
    # Apply minimum image convention to each component
    dr = dr - L * np.round(dr / L)
    # Compute Euclidean distance of the adjusted difference vector
    distance = np.linalg.norm(dr)
    return distance


def E_ij(r, sigma, epsilon, rc):
    '''Calculate the combined truncated and shifted Lennard-Jones potential energy and,
    if specified, the truncated and shifted Yukawa potential energy between two particles.
    Parameters:
    r (float): The distance between particles i and j.
    sigma (float): The distance at which the inter-particle potential is zero for the Lennard-Jones potential.
    epsilon (float): The depth of the potential well for the Lennard-Jones potential.
    rc (float): The cutoff distance beyond which the potentials are truncated and shifted to zero.
    Returns:
    float: The combined potential energy between the two particles, considering the specified potentials.
    '''
    if r >= rc:
        return 0.0
    
    # Calculate Lennard-Jones potential at distance r
    sigma_over_r = sigma / r
    lj_r = 4 * epsilon * (sigma_over_r ** 12 - sigma_over_r ** 6)
    
    # Calculate Lennard-Jones potential at cutoff distance rc
    sigma_over_rc = sigma / rc
    lj_rc = 4 * epsilon * (sigma_over_rc ** 12 - sigma_over_rc ** 6)
    
    # Return truncated and shifted potential
    return lj_r - lj_rc


def E_pot(xyz, L, sigma, epsilon, rc):
    '''Calculate the total potential energy of a system using the truncated and shifted Lennard-Jones potential.
    Parameters:
    xyz : A NumPy array with shape (N, 3) where N is the number of particles. Each row contains the x, y, z coordinates of a particle in the system.
    L (float): Lenght of cubic box
    r (float): The distance between particles i and j.
    sigma (float): The distance at which the inter-particle potential is zero for the Lennard-Jones potential.
    epsilon (float): The depth of the potential well for the Lennard-Jones potential.
    rc (float): The cutoff distance beyond which the potentials are truncated and shifted to zero.
    Returns:
    float
        The total potential energy of the system (in zeptojoules).
    '''
    total_energy = 0.0
    num_particles = xyz.shape[0]
    for i in range(num_particles):
        for j in range(i + 1, num_particles):
            # Calculate minimum image distance between particles i and j
            r = dist(xyz[i], xyz[j], L)
            # Add pairwise Lennard-Jones potential energy to total
            total_energy += E_ij(r, sigma, epsilon, rc)
    return total_energy


def f_ij(r, sigma, epsilon, rc):
    '''Calculate the force vector between two particles, considering both the truncated and shifted
    Lennard-Jones potential and, optionally, the Yukawa potential.
    Parameters:
    r (float): The distance between particles i and j.
    sigma (float): The distance at which the inter-particle potential is zero for the Lennard-Jones potential.
    epsilon (float): The depth of the potential well for the Lennard-Jones potential.
    rc (float): The cutoff distance beyond which the potentials are truncated and shifted to zero.
    Returns:
    array_like: The force vector experienced by particle i due to particle j, considering the specified potentials
    '''
    # Convert displacement to numpy array for consistent operations
    r = np.asarray(r)
    # Calculate scalar distance from displacement vector
    r_mag = np.linalg.norm(r)
    
    # No force if beyond cutoff distance
    if r_mag >= rc:
        return np.zeros_like(r)
    
    # Compute sigma over distance ratio
    sigma_over_r = sigma / r_mag
    
    # Calculate force vector using negative gradient of LJ potential
    # Truncated-shifted potential has same force as original LJ (constant term vanishes in gradient)
    force = 4 * epsilon * (12 * (sigma_over_r)**12 - 6 * (sigma_over_r)**6) * r / (r_mag ** 2)
    
    return force



def velocity_verlet(N, sigma, epsilon, positions, velocities, dt, m, L, rc):
    '''This function runs Velocity Verlet algorithm to integrate the positions and velocities of atoms interacting through
    Lennard Jones Potential forward for one time step according to Newton's Second Law.
    Inputs:
    N: int
       The number of particles in the system.
    sigma: float
       the distance at which Lennard Jones potential reaches zero
    epsilon: float
             potential well depth of Lennard Jones potential
    positions: 2D array of floats with shape (N,3)
              current positions of all atoms, N is the number of atoms, 3 is x,y,z coordinate
    velocities: 2D array of floats with shape (N,3)
              current velocities of all atoms, N is the number of atoms, 3 is x,y,z coordinate
    dt: float
        time step size
    m: float
       mass
    Outputs:
    new_positions: 2D array of floats with shape (N,3)
                   new positions of all atoms, N is the number of atoms, 3 is x,y,z coordinate
    new_velocities: 2D array of floats with shape (N,3)
              current velocities of all atoms, N is the number of atoms, 3 is x,y,z coordinate
    '''
    # Step 1: Calculate current forces and corresponding acceleration
    current_forces = forces(positions, L, sigma, epsilon, rc)
    current_acc = current_forces / m
    
    # Compute velocity at mid-time step t + dt/2
    v_half = velocities + 0.5 * current_acc * dt
    
    # Step 2: Update positions to t + dt
    new_positions = positions + v_half * dt
    # Wrap positions back into the periodic cubic box [0, L)
    new_positions = np.mod(new_positions, L)
    
    # Step 3: Calculate new forces and acceleration based on updated positions
    new_forces = forces(new_positions, L, sigma, epsilon, rc)
    new_acc = new_forces / m
    
    # Step 4: Update velocities to t + dt using mid-step velocity and new acceleration
    new_velocities = v_half + 0.5 * new_acc * dt
    
    return new_positions, new_velocities



def MD_NVT(N, init_positions, init_velocities, L, sigma, epsilon, rc, m, dt, num_steps, T, nu):
    '''Integrate the equations of motion using the velocity Verlet algorithm, with the inclusion of the Berendsen thermostat
    and barostat for temperature and pressure control, respectively.
    Parameters:
    N: int
       The number of particles in the system.
    init_positions: 2D array of floats with shape (N,3)
              current positions of all atoms, N is the number of atoms, 3 is x,y,z coordinate, units: nanometers.
    init_velocities: 2D array of floats with shape (N,3)
              current velocities of all atoms, N is the number of atoms, 3 is x,y,z coordinate, units: nanometers.
    L: float
        Length of the cubic simulation box's side, units: nanometers.
    sigma: float
       the distance at which Lennard Jones potential reaches zero, units: nanometers.
    epsilon: float
             potential well depth of Lennard Jones potential, units: zeptojoules.
    rc: float
        Cutoff radius for potential calculation, units: nanometers.
    m: float
       Mass of each particle, units: grams/mole.
    dt: float
        Integration timestep, units: picoseconds
    num_steps: float
        step number
    T: float
      Current temperature of the particles
    nu: float
      Frequency of the collosion
    Returns:
    tuple
        Updated positions and velocities of all particles, and the possibly modified box length after barostat adjustment.
    '''
    # Convert num_steps to integer to handle float inputs
    num_steps = int(num_steps)
    
    # Initialize current positions and velocities (avoid modifying input arrays)
    positions = np.copy(init_positions)
    velocities = np.copy(init_velocities)
    
    # Initialize arrays to store simulation results
    E_total_array = []
    instant_T_array = []
    intercollision_times = []
    
    # Initialize collision tracking variables
    last_collision_time = None
    
    # Precompute constants for efficiency
    m_particle_kg = (m * 1e-3) / Avogadro  # Convert mass from g/mol to kg per particle
    kB = sp.constants.Boltzmann
    
    # Precompute velocity standard deviation for Maxwell-Boltzmann sampling (constant for fixed T)
    sigma_v_mps = np.sqrt(kB * T / m_particle_kg)
    sigma_v_nmps = sigma_v_mps * 1e-3  # Convert from m/s to nm/ps
    
    # Precompute collision probability per timestep
    collision_prob = 1.0 - np.exp(-nu * dt)
    
    for step_idx in range(num_steps):
        # Step 1: Perform one Velocity Verlet integration step
        positions, velocities = velocity_verlet(N, sigma, epsilon, positions, velocities, dt, m, L, rc)
        
        # Step 2: Apply Anderson Thermostat
        collision_occurred = False
        
        # Vectorized collision check for all particles
        rand_vals = np.random.rand(N)
        collision_mask = rand_vals < collision_prob
        
        if np.any(collision_mask):
            collision_occurred = True
            # Sample new velocities for collided particles from Maxwell-Boltzmann distribution
            num_collisions = np.sum(collision_mask)
            new_vels = np.random.normal(loc=0.0, scale=sigma_v_nmps, size=(num_collisions, 3))
            velocities[collision_mask] = new_vels
        
        # Track inter-collision time intervals
        current_time = (step_idx + 1) * dt
        if collision_occurred:
            if last_collision_time is not None:
                intercollision_interval = current_time - last_collision_time
                intercollision_times.append(intercollision_interval)
            last_collision_time = current_time
        
        # Step 3: Calculate total energy and instantaneous temperature
        # Kinetic energy calculation (convert to SI units first)
        v_si = velocities * 1e3  # Convert velocity from nm/ps to m/s
        kinetic_J = 0.5 * m_particle_kg * np.sum(v_si ** 2)
        kinetic_zj = kinetic_J * 1e21  # Convert kinetic energy from Joules to zeptojoules
        
        # Potential energy calculation using the pre-defined E_pot function
        potential_zj = E_pot(positions, L, sigma, epsilon, rc)
        total_energy = kinetic_zj + potential_zj
        E_total_array.append(total_energy)
        
        # Instantaneous temperature calculation from kinetic energy
        instant_T = (2 * kinetic_J) / (3 * N * kB)
        instant_T_array.append(instant_T)
    
    # Convert result lists to numpy arrays for consistency and easier post-processing
    E_total_array = np.array(E_total_array)
    instant_T_array = np.array(instant_T_array)
    intercollision_times = np.array(intercollision_times)
    
    return (E_total_array, instant_T_array, positions, velocities, intercollision_times)
